import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import SideBar from '../common/sideBar/SideBar';
import InvitationItem from './InvitationItem.js';
import FriendItem from './FriendItem.js';
import { friendActions } from '../../actions';

class FriendsPage extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            isLoading: true
        }
    }

    componentDidMount(){
        this.setState({isLoading: true})
        this.props.dispatch(friendActions.getInvitations());
        this.props.dispatch(friendActions.getConfirmed());
        this.setState({isLoading: false})
    }

    render() {
        const { friends, friendsInvitations, friendsConfirmed } = this.props;

        if(this.state.isLoading)
        {
            return (
                <img alt="spinner" src="data:image/gif;base64," />
            );
        }

        return (
            <section className="container">
                <div className="row ">
                    <SideBar />
                    <div className="col-md-9 content profile-changes">
                        <div>Zaproszenia do znajomych : 
                            {!friendsInvitations || friendsInvitations.loadingInvitations ? 
                            (
                                <div>
                                    <img alt="loading" src="data:image/gif;base64," />
                                </div>
                            ) : (
                                <div>
                                    {friendsInvitations.friendInvitations.map((friend,indeks) => 
                                        <InvitationItem friend={friend} />
                                    )}
                                </div>
                            )}
                        </div>
                        <div>Znajomi : 
                            {!friendsConfirmed || friendsConfirmed.loadingConfirmedFriends ? 
                            (
                                <div>
                                    <img alt="loading" src="data:image/gif;base64," />
                                </div>
                            ) : (
                                <div>
                                    {friendsConfirmed.confirmedFirends.map((friend,indeks) => 
                                        <FriendItem friend={friend} />
                                    )}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}


function mapStateToProps(state, ownProps) {
    const {friendsInvitations, friendsConfirmed } = state;
    return {
        friendsInvitations,
        friendsConfirmed
    };
}

export default connect(mapStateToProps)(FriendsPage);